const mongoose = require('mongoose');
const Shema = mongoose.Schema;

const shema = new Shema({
    title: {
        type: String,
        required: true
    },
    body: {
        type: String
    }
});
// console.log('shema :-: ', shema);
// console.log('model :-: ', mongoose.model('Post', shema));
module.exports = mongoose.model('Post', shema);